mapper.select:
select * from t_sys_member where
mobilePhone = #{mobilephone,jdbcType=VARCHAR}
and
`password` = #{password,jdbcType=VARCHAR}

select t.*
	from t_sys_member t
	where id = #{id,jdbcType=INTEGER}

mapper.update:
update t_member_browsing

    createdate = NOW(),

where id = #{id,jdbcType=INTEGER}

mapper.select:
select t.* from t_member_browsing t
t.memberid=#{memberBrowsing.memberid}
order by t.createdate DESC
limit #{start}, #{limit}

select t.* from t_member_browsing t

	t.memberid=#{memberid,jdbcType=INTEGER}
	and 
	t.type=#{type,jdbcType=VARCHAR}
	and
	t.browsingid = #{browsingid,jdbcType=INTEGER}
